function Fechas()
{this.generarMeses=function()
{var meses=["Enero","Febrero","Marzo","Abril","Mayo","Junio","Julio","Agosto","Septiembre","Octubre","Noviembre","Diciembre"];return meses}
this.generarDias=function()
{var dias=["Domingo","Lunes","Martes","Miercoles","Jueves","Viernes","Sabado"];return dias}
this.generarDiaMaxPorMes=function(anio)
{var dias=[31,(((anio%4)==0)?29:28),31,30,31,30,31,31,30,31,30,31];return dias}
this.obtenerNombreMes=function(num_mes)
{var meses=this.generarMeses();return meses[(num_mes-1)]}
this.obtenerNombreDia=function(num_dia)
{var dias=this.generarDias();if(num_dia==7)num_dia=0;return dias[num_dia]}
this.obtenerDiaMaxMes=function(num_mes,anio)
{var dias=this.generarDiaMaxPorMes(anio);return dias[(num_mes-1)]}
this.obtenerNumeroDiaSemana=function(fecha)
{var partes=fecha.split('/');var xfecha=new Date(parseInt(partes[2]),(parseInt(partes[1])-1),parseInt(partes[0]),0,0,0);var dia_semana=xfecha.getDay();return dia_semana}
this.formatearFechaLarga=function(fecha)
{var dia_semana=this.obtenerNumeroDiaSemana(fecha);var partes=fecha.split('/');var fecha_larga=this.obtenerNombreDia(dia_semana)+', '+partes[0]+' de '+this.obtenerNombreMes(partes[1])+' de '+partes[2];return fecha_larga}
this.formatearFechaDmy=function(fecha)
{var dia=fecha.getDate().toString();var mes=(fecha.getMonth()+1);var retorno='';mes=mes.toString();if(dia.length<2)dia='0'+dia;if(mes.length<2)mes='0'+mes;retorno=dia+'/'+mes+'/'+fecha.getFullYear().toString();return retorno}
this.convertirFechaYmd=function(fecha,separador)
{var fecha_amd='';var mes_fecha=fecha.getMonth()+1;var dia_fecha=fecha.getDate();if(mes_fecha<10)mes_fecha='0'+mes_fecha.toString();if(dia_fecha<10)dia_fecha='0'+dia_fecha.toString();fecha_amd=fecha.getFullYear().toString()+separador+mes_fecha+separador+dia_fecha;return fecha_amd}
this.fechaEsHoy=function(fecha,separador)
{var retorno=!1;var hoy=new Date(),retorno=!1;var hoy_amd=this.convertirFechaYmd(hoy,separador);if(hoy_amd==fecha)retorno=!0;return retorno}}