Vue.component('vue-autocomplete',{template:'<div><input v-bind:class="class_input" type="text" '+'v-bind:value="value" v-on:input="updateValue($event.target.value)" '+'v-on:keydown.enter="enter" v-on:keydown.down="down" v-on:keydown.up="up" '+'v-bind:placeholder="placeholder">'+'<ul v-show="open" v-bind:class="class_ul" '+'style="position: absolute; z-index: 50;">'+'   <template v-if="matches.length > 0">'+'       <li v-for="(item, index) in matches" '+'       v-bind:class="[ isActive(index) ? class_active : \'\' ]" '+'       v-on:click="suggestionClick(index)">'+'           <a href="javascript:void(0)" '+'           style="text-decoration: none">{{ item }}</a>'+'       </li>'+'   </template>'+'   <li v-else>No hay resultados</li>'+'</ul></div>',props:{value:{type:String,required:!0},suggestions:{type:Array,required:!0},min_len:{type:String,required:!0},placeholder:{type:String,required:!1},class_input:{type:String,required:!1},class_ul:{type:String,required:!1},class_active:{type:String,required:!0}},data:function(){return{open:!1,current:0}},computed:{matches:function(){var resultado=[];if(this.value.length>=parseInt(this.min_len))
{resultado=this.suggestions.filter(this.buscarCoincidencias)}
return resultado}},methods:{updateValue:function(value){if(this.open===!1){this.open=!0;this.current=0}
this.$emit('input',value)},enter:function(){this.$emit('input',this.matches[this.current]);this.open=!1},up:function(){if(this.current>0)this.current--},down:function(){if(this.current<this.matches.length-1)this.current++},isActive:function(index){return index===this.current},suggestionClick:function(index){this.$emit('input',this.matches[index]);this.open=!1},buscarCoincidencias:function(valor){var patt=new RegExp(this.value,"i");var condicion=valor.search(patt)>=0;return condicion}}})
